select mm.*,
mm.uuid as uuid,
user_name AS userName
from cipher_user_info mm
where 1=1 and mm.is_delete=0 and mm.account_authorized_date is null and mm.account_authorized_valid_date is null
and mm.company_id=#{companyId}

    and (user_name LIKE CONCAT(CONCAT('%',#{queryName}),'%') OR
    account_number LIKE CONCAT(CONCAT('%',#{queryName}),'%'))


    and mm.account_number=#{accountNumber}


    and account_status=#{status}


    order by account_number ${sord}


    order by user_name ${sord}


LIMIT #{pageData.currentRow}, #{pageData.rows}

select count(1)
from cipher_user_info mm
where 1=1 and mm.is_delete=0 and mm.account_authorized_date is null and mm.account_authorized_valid_date is null
and mm.company_id=#{companyId}

    and (user_name LIKE CONCAT(CONCAT('%',#{queryName}),'%') OR
    account_number LIKE CONCAT(CONCAT('%',#{queryName}),'%'))


    and mm.account_number=#{accountNumber}


    and account_status=#{status}

select account_number as accountNumber, user_name AS userName, role_name as roleName, account_status as status from cipher_user_info mm LEFT join cipher_role_user_map tt on mm.account_number=tt.user_id LEFT JOIN cipher_role_info nn on nn.id=tt.role_id where 1=1 and mm.is_delete=1 and mm.account_authorized_date is null and mm.account_authorized_valid_date is null
and user_name LIKE CONCAT(CONCAT('%',#{queryName}),'%') OR account_number LIKE CONCAT(CONCAT('%',#{queryName}),'%')
order by account_number ${sord}
order by user_name ${sord}
order by role_name ${sord}
LIMIT #{pageData.currentRow}, #{pageData.rows}

select mm.uuid as uuid, mm.account_number as accountNumber, user_name AS userName, source, ff.group_id as groupId, account_status as status from cipher_user_info mm Left JOIN cipher_group_user_map ff on ff.user_id=mm.uuid Left join cipher_group_info rr on rr.group_id=ff.group_id where 1=1 and mm.is_delete=0 and mm.account_authorized_date is null and mm.account_authorized_valid_date is null and rr.group_id=#{groupId}
and (user_name LIKE CONCAT(CONCAT('%',#{queryName}),'%') OR mm.account_number LIKE CONCAT(CONCAT('%',#{queryName}),'%'))
and account_status=#{status}
order by account_number ${sord}
order by user_name ${sord}

select mm.*,
mm.uuid as uuid,
user_name AS userName
from cipher_user_info mm
Left JOIN cipher_group_user_map ff on ff.user_id=mm.uuid
WHERE ff.group_id=0 and mm.is_delete=0 and mm.company_id=#{companyId}

    and (user_name LIKE CONCAT(CONCAT('%',#{queryName}),'%') OR
    mm.account_number LIKE CONCAT(CONCAT('%',#{queryName}),'%'))


    and account_status=#{status}

LIMIT #{pageData.currentRow}, #{pageData.rows}

select count(1) from cipher_user_info mm Left JOIN cipher_group_user_map ff on ff.user_id=mm.uuid WHERE ff.group_id=0 and mm.is_delete=0 and mm.company_id=#{companyId}
and (user_name LIKE CONCAT(CONCAT('%',#{queryName}),'%') OR mm.account_number LIKE CONCAT(CONCAT('%',#{queryName}),'%'))
and account_status=#{status}

select mm.*, mm.uuid AS uuid, mm.user_name AS userName, ff.group_id as groupId from cipher_user_info mm Left JOIN cipher_group_user_map ff on ff.user_id=mm.uuid WHERE mm.is_delete=0 and mm.account_authorized_date is null and mm.account_authorized_valid_date is null and mm.company_id=#{companyId}
and (user_name LIKE CONCAT(CONCAT('%',#{queryName}),'%') OR mm.account_number LIKE CONCAT(CONCAT('%',#{queryName}),'%'))
and mm.account_number=#{accountNumber}
and account_status=#{status}
group by mm.uuid

