select mm.uuid as uuid, mm.account_number as accountNumber, user_name AS userName, source, ff.group_id as groupId, account_status as status from cipher_user_info mm Left JOIN cipher_group_user_map ff on ff.user_id=mm.uuid where 1=1 and mm.is_delete=0 and mm.account_authorized_date is null and mm.account_authorized_valid_date is null and ff.group_id=0
and (user_name LIKE CONCAT(CONCAT('%',#{queryName}),'%') OR mm.account_number LIKE CONCAT(CONCAT('%',#{queryName}),'%'))
and account_status=#{status}
order by account_number ${sord}
order by user_name ${sord}
